#!/bin/bash

test_case() {
    ARG="$1"
    echo "\033[37mTesting ARG = \033[36m$ARG\033[93m"
    echo "\033[37mCount of operations:\033[37m\033[95m"
    ./push_swap $ARG | wc -l
    checker_result=$(./push_swap $ARG | ./checker_linux $ARG)
    if [ "$checker_result" = "OK" ]; then
        echo "\033[37mChecker result: \033[37m\033[92m$checker_result\033[0m"
    else
        echo "\033[37mChecker result: \033[37m\033[91m$checker_result\033[0m"
    fi
    echo "\033[0m"
    sleep 1
}
